import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
    const location = useLocation();

    const navigationItems = [
        {
            path: '/',
            icon: 'ðŸ ',
            label: 'Dashboard',
            description: 'Overview and activities'
        },
        {
            path: '/preferences',
            icon: 'âš™ï¸',
            label: 'Preferences',
            description: 'Settings and customization'
        }
    ];

    const isActive = (path) => {
        if (path === '/') {
            return location.pathname === '/';
        }
        return location.pathname.startsWith(path);
    };

    return (
        <>
            <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
                {/* Logo Section */}
                <div className="sidebar-header">
                    <div className="logo">
                        <span className="logo-icon">âœ±</span>
                        {!isCollapsed && <span className="logo-text">logo</span>}
                    </div>
                </div>

                {/* Navigation */}
                <nav className="sidebar-nav">
                    {navigationItems.map((item) => (
                        <Link
                            key={item.path}
                            to={item.path}
                            className={`nav-item ${isActive(item.path) ? 'active' : ''}`}
                            title={isCollapsed ? item.label : ''}
                        >
                            <span className="nav-icon">{item.icon}</span>
                            {!isCollapsed && (
                                <div className="nav-content">
                                    <span className="nav-label">{item.label}</span>
                                    <span className="nav-description">{item.description}</span>
                                </div>
                            )}
                        </Link>
                    ))}
                </nav>

                {/* User Profile Section */}
                <div className="sidebar-footer">
                    <div className="user-profile">
                        <div className="user-avatar">
                            <span>ðŸ‘¤</span>
                        </div>
                        {!isCollapsed && (
                            <div className="user-info">
                                <div className="user-name">User Profile</div>
                                <div className="user-status">Personalized Experience</div>
                            </div>
                        )}
                    </div>
                </div>

                {/* Collapse Toggle */}
                <button 
                    className="collapse-toggle"
                    onClick={() => setIsCollapsed(!isCollapsed)}
                    title={isCollapsed ? 'Expand sidebar' : 'Collapse sidebar'}
                >
                    <span className={`toggle-icon ${isCollapsed ? 'collapsed' : ''}`}>
                        â—€
                    </span>
                </button>
            </div>

            {/* Overlay for mobile */}
            <div className={`sidebar-overlay ${!isCollapsed ? 'active' : ''}`} 
                 onClick={() => setIsCollapsed(true)}>
            </div>
        </>
    );
};

export default Sidebar;
