import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import userPreferences from '../utils/UserPreferences';
import './PlaceDetail.css';

const PlaceDetail = () => {
    const { placeId } = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const [place, setPlace] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isLiked, setIsLiked] = useState(false);
    const [isDisliked, setIsDisliked] = useState(false);

    useEffect(() => {
        fetchPlaceDetails();
    }, [placeId]);

    const fetchPlaceDetails = async () => {
        try {
            setLoading(true);

            // First try to get place data from navigation state
            const statePlace = location.state?.place;
            const activityName = location.state?.activityName;

            if (statePlace && statePlace.place_id) {
                // Fetch detailed data from backend
                console.log('Fetching place details for:', statePlace.place_id);
                const response = await axios.get(
                    `http://127.0.0.1:8000/api/place-details/${statePlace.place_id}/`
                );

                const detailedPlace = {
                    ...response.data,
                    activityName: activityName || statePlace.activityName
                };

                setPlace(detailedPlace);

                // Check user preferences
                checkUserPreferences(detailedPlace.place_id);

            } else if (placeId) {
                // Fetch directly by place ID
                console.log('Fetching place details by ID:', placeId);
                const response = await axios.get(
                    `http://127.0.0.1:8000/api/place-details/${placeId}/`
                );

                setPlace(response.data);
                checkUserPreferences(placeId);

            } else {
                throw new Error('No place ID available');
            }

        } catch (error) {
            console.error('Error fetching place details:', error);
            // Fallback to mock data
            setPlace(getMockPlaceData(placeId));
            checkUserPreferences(placeId);
        } finally {
            setLoading(false);
        }
    };

    const checkUserPreferences = (currentPlaceId) => {
        // Check local preferences first
        const likedPlaces = userPreferences.getLikedPlaces();
        const dislikedPlaces = userPreferences.getDislikedPlaces();

        const isPlaceLiked = likedPlaces.some(p => p.place_id === currentPlaceId);
        const isPlaceDisliked = dislikedPlaces.some(p => p.place_id === currentPlaceId);

        setIsLiked(isPlaceLiked);
        setIsDisliked(isPlaceDisliked);
    };

    const getMockPlaceData = (id) => {
        return {
            place_id: id,
            name: 'Mount Cinder Viewpoint',
            vicinity: 'Cascade Mountains, OR',
            description: 'Mount Cinder Viewpoint offers breathtaking panoramic views of the entire Cascade Mountain range, including the iconic peaks of Mount Hood and Mount Jefferson. It\'s a prime location for photography, bird watching, and enjoying serene sunsets. The well-maintained trail to the viewpoint is accessible for most skill levels, making it a popular spot for both casual visitors and avid hikers. The area here is crisp and clean, providing a refreshing escape from city life. Visitors often bring picnics to enjoy the natural beauty.',
            photos: ['https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=400&fit=crop'],
            rating: 4.7,
            user_ratings_total: 53,
            opening_hours: {
                weekday_text: [
                    'Monday: 6:00 AM - 6:00 PM',
                    'Tuesday: 6:00 AM - 6:00 PM',
                    'Wednesday: 6:00 AM - 6:00 PM',
                    'Thursday: 6:00 AM - 6:00 PM',
                    'Friday: 9:00 AM - 8:00 PM',
                    'Saturday: 8:00 AM - 8:00 PM',
                    'Sunday: 8:00 AM - 6:00 PM'
                ]
            },
            formatted_address: '2500 Viewpoint Rd, Cascade Mountains, OR 97000',
            formatted_phone_number: '(555) 123-4567',
            website: 'mountcinderviewpoint.org',
            types: ['tourist_attraction', 'park'],
            activityName: 'park'
        };
    };

    const handleLikeToggle = async () => {
        if (!place) return;

        try {
            const newIsLiked = !isLiked;
            const preference = newIsLiked ? 'like' : 'dislike';

            // Update backend
            await updateBackendPreference(preference);

            // Update local state and storage
            if (newIsLiked) {
                // Like the place
                userPreferences.likePlace(place, place.activityName || 'general');
                setIsLiked(true);
                setIsDisliked(false);
            } else {
                // Remove like (neutral state)
                userPreferences.removePlacePreference(place.place_id);
                setIsLiked(false);
            }
        } catch (error) {
            console.error('Error updating place preference:', error);
        }
    };

    const handleDislikeToggle = async () => {
        if (!place) return;

        try {
            const newIsDisliked = !isDisliked;
            const preference = newIsDisliked ? 'dislike' : 'like';

            // Update backend
            await updateBackendPreference(preference);

            // Update local state and storage
            if (newIsDisliked) {
                // Dislike the place
                userPreferences.dislikePlace(place, place.activityName || 'general');
                setIsDisliked(true);
                setIsLiked(false);
            } else {
                // Remove dislike (neutral state)
                userPreferences.removePlacePreference(place.place_id);
                setIsDisliked(false);
            }
        } catch (error) {
            console.error('Error updating place preference:', error);
        }
    };

    const updateBackendPreference = async (preference) => {
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user-preference/', {
                place_id: place.place_id,
                place_name: place.name,
                activity_type: place.activityName || 'general',
                preference: preference,
                user_id: 'user123' // In a real app, this would be the actual user ID
            });

            console.log('Backend preference updated:', response.data);
        } catch (error) {
            console.error('Failed to update backend preference:', error);
            throw error;
        }
    };

    const formatOpeningHours = () => {
        if (!place?.opening_hours?.weekday_text) {
            return [
                { day: 'Monday', hours: '6:00 AM - 6:00 PM' },
                { day: 'Tuesday', hours: '6:00 AM - 6:00 PM' },
                { day: 'Wednesday', hours: '6:00 AM - 6:00 PM' },
                { day: 'Thursday', hours: '6:00 AM - 6:00 PM' },
                { day: 'Friday', hours: '9:00 AM - 8:00 PM' },
                { day: 'Saturday', hours: '8:00 AM - 8:00 PM' },
                { day: 'Sunday', hours: '8:00 AM - 6:00 PM' }
            ];
        }

        return place.opening_hours.weekday_text.map(dayText => {
            const [day, ...hoursParts] = dayText.split(': ');
            return {
                day: day,
                hours: hoursParts.join(': ')
            };
        });
    };

    if (loading) {
        return <div className="loading">Loading place details...</div>;
    }

    if (!place) {
        return <div className="error">Place not found</div>;
    }

    return (
        <div className="place-detail">
            {/* Header */}
            <div className="place-header">
                <button className="back-button" onClick={() => navigate(-1)}>
                    ‚Üê Back to Dashboard
                </button>
                <div className="place-actions">
                    <button 
                        className={`favorite-btn ${isLiked ? 'liked' : ''}`}
                        onClick={handleLikeToggle}
                        title={isLiked ? 'Remove from favorites' : 'Add to favorites'}
                    >
                        {isLiked ? '‚ù§Ô∏è' : 'ü§ç'}
                    </button>
                    <button 
                        className={`dislike-btn ${isDisliked ? 'disliked' : ''}`}
                        onClick={handleDislikeToggle}
                        title={isDisliked ? 'Remove dislike' : 'Not interested'}
                    >
                        {isDisliked ? 'üíî' : 'üëé'}
                    </button>
                    <button className="share-btn">üì§</button>
                </div>
            </div>

            {/* Hero Image */}
            <div className="place-hero">
                <img 
                    src={place.photos?.[0] || place.image || 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=400&fit=crop'} 
                    alt={place.name} 
                />
            </div>

            {/* Main Content */}
            <div className="place-content">
                <div className="place-title-section">
                    <h1>{place.name}</h1>
                    <p className="place-location">{place.vicinity || place.location || 'Location not specified'}</p>
                </div>

                {/* Overview */}
                <section className="overview-section">
                    <h2>Overview</h2>
                    <div className="about-section">
                        <h3>About This Place</h3>
                        <p>{place.description || 'A wonderful place to visit and explore.'}</p>
                    </div>
                </section>

                {/* Opening Hours */}
                <section className="hours-section">
                    <h3>Opening Hours</h3>
                    <div className="hours-grid">
                        {formatOpeningHours().map((dayInfo, index) => (
                            <div key={index} className="hours-row">
                                <span>{dayInfo.day}:</span>
                                <span>{dayInfo.hours}</span>
                            </div>
                        ))}
                    </div>
                </section>

                {/* Contact Information */}
                <section className="contact-section">
                    <h3>Contact Information</h3>
                    <div className="contact-info">
                        {(place.formatted_address || place.contact?.address) && (
                            <div className="contact-item">
                                <span className="contact-icon">üìç</span>
                                <span>{place.formatted_address || place.contact.address}</span>
                            </div>
                        )}
                        {(place.formatted_phone_number || place.contact?.phone) && (
                            <div className="contact-item">
                                <span className="contact-icon">ÔøΩ</span>
                                <span>{place.formatted_phone_number || place.contact.phone}</span>
                            </div>
                        )}
                        {(place.website || place.contact?.website) && (
                            <div className="contact-item">
                                <span className="contact-icon">üåê</span>
                                <span>{place.website || place.contact.website}</span>
                            </div>
                        )}
                    </div>
                </section>

                {/* Key Features */}
                <section className="features-section">
                    <h3>Key Features</h3>
                    <div className="features-list">
                        {(place.features || place.types || ['Restaurant', 'Outdoor Seating', 'Pet-friendly']).map((feature, index) => (
                            <span key={index} className="feature-tag">
                                {feature.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                            </span>
                        ))}
                    </div>
                </section>

                {/* User Reviews */}
                <section className="reviews-section">
                    <div className="reviews-header">
                        <h3>User Reviews</h3>
                        <div className="reviews-summary">
                            <span className="rating-display">
                                ‚≠ê {place.rating || 4.0} ({place.user_ratings_total || place.reviews || 0} Reviews)
                            </span>
                            <button className="write-review-btn">‚úçÔ∏è Write a Review</button>
                        </div>
                    </div>

                    <div className="reviews-list">
                        {(place.userReviews || [
                            {
                                id: 1,
                                author: 'Alex Johnson',
                                date: 'April 15, 2024',
                                rating: 5,
                                comment: 'Great place! Highly recommend visiting.'
                            }
                        ]).map((review) => (
                            <div key={review.id} className="review-item">
                                <div className="review-header">
                                    <h4>{review.author}</h4>
                                    <span className="review-date">{review.date}</span>
                                </div>
                                <div className="review-rating">
                                    {'‚≠ê'.repeat(review.rating)}
                                </div>
                                <p className="review-comment">{review.comment}</p>
                            </div>
                        ))}
                    </div>
                </section>
            </div>
        </div>
    );
};

export default PlaceDetail;
