import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import userPreferences from '../utils/UserPreferences';
import { convertTemperature, convertDistance, formatTemperature, formatDistance, convertSpeed, formatSpeed } from '../utils/UnitConverter';
import './Dashboard.css';

const Dashboard = ({ appSettings }) => {
    const navigate = useNavigate();
    const [weatherData, setWeatherData] = useState(null);
    const [activities, setActivities] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentLocation, setCurrentLocation] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState('All Activities');

    const activityCategories = [
        'All Activities',
        'Outdoor Adventures', 
        'Relaxing Indoors',
        'Cultural Experiences',
        'Family Fun'
    ];

    const mockActivities = [
        {
            id: 1,
            name: 'Central Park Picnic',
            category: 'Outdoor Adventures',
            description: 'Enjoy a beautiful day out with scenic views and fresh air.',
            image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=250&fit=crop',
            rating: 4.8,
            reviews: 125,
            duration: '2-4 hours',
            difficulty: 'Easy'
        },
        {
            id: 2,
            name: 'Museum Hopping',
            category: 'Cultural Experiences',
            description: 'Explore world-class art and history galleries.',
            image: 'https://images.unsplash.com/photo-1554907984-15263bfd63bd?w=400&h=250&fit=crop',
            rating: 4.6,
            reviews: 89,
            duration: '3-5 hours',
            difficulty: 'Easy'
        },
        {
            id: 3,
            name: 'Cozy Coffee Shops',
            category: 'Relaxing Indoors',
            description: 'Relax with a warm beverage and a good book.',
            image: 'https://images.unsplash.com/photo-1501339847302-ac426a4a7cbb?w=400&h=250&fit=crop',
            rating: 4.4,
            reviews: 67,
            duration: '1-2 hours',
            difficulty: 'Easy'
        },
        {
            id: 4,
            name: 'Bike Rentals',
            category: 'Outdoor Adventures',
            description: 'Discover the city on two wheels, explore hidden gems.',
            image: 'https://images.unsplash.com/photo-1558531304-a4773b7e3a9e?w=400&h=250&fit=crop',
            rating: 4.7,
            reviews: 156,
            duration: '2-6 hours',
            difficulty: 'Medium'
        },
        {
            id: 5,
            name: 'Indoor Rock Climbing',
            category: 'Outdoor Adventures',
            description: 'Challenge yourself with exciting routes and friendly community.',
            image: 'https://images.unsplash.com/photo-1522163182402-834f871fd851?w=400&h=250&fit=crop',
            rating: 4.5,
            reviews: 203,
            duration: '1-3 hours',
            difficulty: 'Hard'
        },
        {
            id: 6,
            name: 'Local Farmers Market',
            category: 'Family Fun',
            description: 'Discover fresh produce and artisan goods.',
            image: 'https://images.unsplash.com/photo-1488459716781-31db52582fe9?w=400&h=250&fit=crop',
            rating: 4.3,
            reviews: 78,
            duration: '1-2 hours',
            difficulty: 'Easy'
        },
        {
            id: 7,
            name: 'Virtual Reality Arcade',
            category: 'Family Fun',
            description: 'Immerse yourself in new worlds and experiences.',
            image: 'https://images.unsplash.com/photo-1592478411213-6153e4ebc07d?w=400&h=250&fit=crop',
            rating: 4.6,
            reviews: 134,
            duration: '1-2 hours',
            difficulty: 'Easy'
        },
        {
            id: 8,
            name: 'Gourmet Cooking Class',
            category: 'Relaxing Indoors',
            description: 'Learn new culinary skills and savor delicious creations.',
            image: 'https://images.unsplash.com/photo-1556909114-f6e7ad7d3136?w=400&h=250&fit=crop',
            rating: 4.9,
            reviews: 92,
            duration: '2-3 hours',
            difficulty: 'Medium'
        }
    ];

    // Transform backend activity data to match frontend format
    const transformActivitiesFromBackend = (backendActivities) => {
        return backendActivities.map((activity, index) => {
            const firstPlace = activity.places && activity.places.length > 0 ? activity.places[0] : null;
            return {
                id: index + 1,
                name: activity.activity_name || 'Activity',
                category: mapActivityToCategory(activity.activity_name),
                description: firstPlace ? firstPlace.description || `Discover ${activity.activity_name} in your area` : `Explore ${activity.activity_name}`,
                image: firstPlace && firstPlace.photos && firstPlace.photos.length > 0 
                    ? firstPlace.photos[0] 
                    : `https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=250&fit=crop`, // fallback image
                rating: firstPlace ? firstPlace.rating || 4.0 : 4.0,
                reviews: firstPlace ? firstPlace.user_ratings_total || 0 : 0,
                duration: '1-3 hours', // Default duration
                difficulty: 'Easy',
                activity_name: activity.activity_name,
                place_count: activity.places ? activity.places.length : 0,
                places: activity.places || []
            };
        });
    };

    // Map activity names to our frontend categories
    const mapActivityToCategory = (activityName) => {
        if (!activityName) return 'All Activities';

        const categoryMap = {
            'park': 'Outdoor Adventures',
            'hiking': 'Outdoor Adventures', 
            'restaurant': 'Cultural Experiences',
            'museum': 'Cultural Experiences',
            'cafe': 'Relaxing Indoors',
            'shopping': 'Family Fun',
            'cinema': 'Relaxing Indoors',
            'gym': 'Outdoor Adventures',
            'library': 'Relaxing Indoors'
        };

        const lowerActivity = activityName.toLowerCase();
        for (const [key, category] of Object.entries(categoryMap)) {
            if (lowerActivity.includes(key)) {
                return category;
            }
        }

        return 'All Activities';
    };

    useEffect(() => {
        fetchWeatherAndActivities();
    }, []);

    const fetchWeatherAndActivities = async () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                async (position) => {
                    const { latitude, longitude } = position.coords;
                    setCurrentLocation({ lat: latitude, lng: longitude });

                    try {
                        // Fetch activities and weather data from the activity suggestion API
                        const activityResponse = await axios.post(
                            'http://127.0.0.1:8000/api/activity-suggestion/',
                            { 
                                latitude, 
                                longitude,
                                max_activities: 8  // Get more activities for variety
                            },
                            { 
                                headers: { 'Content-Type': 'application/json' } 
                            }
                        );

                        if (activityResponse.data && activityResponse.data.activities) {
                            // Transform backend activities to match frontend format
                            const transformedActivities = transformActivitiesFromBackend(activityResponse.data.activities);

                            // Sort activities by user preferences
                            const sortedActivities = userPreferences.sortActivitiesByPreference(transformedActivities);
                            setActivities(sortedActivities);

                            // Set weather data from the same response
                            if (activityResponse.data.weather) {
                                setWeatherData(activityResponse.data.weather);
                            }
                        }
                    } catch (error) {
                        console.error('Activity fetch error:', error);
                        // Fallback to weather-only API if activity API fails
                        try {
                            const weatherResponse = await axios.post(
                                'http://127.0.0.1:8000/api/suggestions/',
                                { latitude, longitude },
                                { headers: { 'Content-Type': 'application/x-www-form-urlencoded' } }
                            );
                            setWeatherData(weatherResponse.data);
                        } catch (weatherError) {
                            console.error('Weather fetch error:', weatherError);
                            // Mock weather data for demo
                            setWeatherData({
                                name: 'New York City',
                                main: { temp: 22, feels_like: 24, humidity: 65 },
                                weather: [{ description: 'sunny with clear skies', main: 'Clear' }],
                                wind: { speed: 10 },
                                visibility: 10000,
                                uv: { value: 6 }
                            });
                        }

                        // Use mock activities as fallback
                        const sortedActivities = userPreferences.sortActivitiesByPreference(
                            mockActivities.map(activity => ({
                                ...activity,
                                activity_name: activity.category.toLowerCase().replace(' ', '_'),
                                place_count: 1,
                                places: [activity]
                            }))
                        );
                        setActivities(sortedActivities);
                    }
                    setLoading(false);
                },
                (error) => {
                    console.error('Geolocation error:', error);
                    // Set mock data anyway
                    setWeatherData({
                        name: 'Current Location',
                        main: { temp: 22, feels_like: 24, humidity: 65 },
                        weather: [{ description: 'sunny with clear skies', main: 'Clear' }],
                        wind: { speed: 10 },
                        visibility: 10000
                    });
                    setActivities(mockActivities);
                    setLoading(false);
                }
            );
        }
    };

    const getFilteredActivities = () => {
        if (selectedCategory === 'All Activities') {
            return activities;
        }
        return activities.filter(activity => activity.category === selectedCategory);
    };

    const getTemperature = (temp) => {
        if (!appSettings?.units?.temperature || !temp) return Math.round(temp);
        // Weather API returns Celsius, convert if needed
        const convertedTemp = convertTemperature(temp, 'Celsius (¬∞C)', appSettings.units.temperature);
        return convertedTemp;
    };

    const getTemperatureUnit = () => {
        return appSettings?.units?.temperature === 'Fahrenheit (¬∞F)' ? '¬∞F' : '¬∞C';
    };

    const getWindSpeed = () => {
        if (!weatherData?.wind?.speed) return 10;
        // Weather API returns m/s, convert to appropriate unit
        const speedInKmh = weatherData.wind.speed * 3.6; // Convert m/s to km/h
        if (appSettings?.units?.distance === 'Miles (mi)') {
            const speedInMph = convertSpeed(speedInKmh, 'Kilometers (km)', 'Miles (mi)');
            return Math.round(speedInMph);
        }
        return Math.round(speedInKmh);
    };

    const getSpeedUnit = () => {
        return appSettings?.units?.distance === 'Miles (mi)' ? 'mph' : 'km/h';
    };

    const getVisibility = () => {
        if (!weatherData?.visibility) return 6;
        const visibilityInKm = weatherData.visibility / 1000; // Convert meters to km
        if (appSettings?.units?.distance === 'Miles (mi)') {
            return Math.round(convertDistance(visibilityInKm, 'Kilometers (km)', 'Miles (mi)'));
        }
        return Math.round(visibilityInKm);
    };

    const getDistanceUnit = () => {
        return appSettings?.units?.distance === 'Miles (mi)' ? 'mi' : 'km';
    };

    if (loading) {
        return (
            <div className="dashboard-loading">
                <div className="loading-spinner"></div>
                <p>Loading your personalized recommendations...</p>
            </div>
        );
    }

    return (
        <div className="dashboard">
            {/* Top Header */}
            <div className="dashboard-header">
                <div className="header-content">
                    <h1>Dashboard</h1>
                    <div className="header-actions">
                        <div className="search-bar">
                            <input type="text" placeholder="Search activities or places..." />
                            <button className="search-btn">üîç</button>
                        </div>
                        <div className="user-profile">
                            <div className="user-avatar">üë§</div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Weather Section */}
            <div className="weather-section">
                <h2>Current Weather & Conditions</h2>
                <div className="weather-main">
                    <div className="temperature">
                        <span className="temp-value">{getTemperature(weatherData?.main?.temp || 22)}{getTemperatureUnit()}</span>
                        <p className="weather-description">
                            {weatherData?.weather?.[0]?.description || 'Sunny with clear skies'}
                        </p>
                        <p className="location">{weatherData?.name || 'Current Location'} ‚Ä¢ Today, {new Date().toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' })}</p>
                    </div>

                    <div className="weather-details">
                        <div className="weather-stat">
                            <span className="stat-label">Feels like</span>
                            <span className="stat-value">{getTemperature(weatherData?.main?.feels_like || 24)}{getTemperatureUnit()}</span>
                        </div>
                        <div className="weather-stat">
                            <span className="stat-label">Humidity</span>
                            <span className="stat-value">{weatherData?.main?.humidity || 65}%</span>
                        </div>
                        <div className="weather-stat">
                            <span className="stat-label">Wind</span>
                            <span className="stat-value">{getWindSpeed()} {getSpeedUnit()}</span>
                        </div>
                        <div className="weather-stat">
                            <span className="stat-label">UV Index</span>
                            <span className="stat-value">{weatherData?.uv?.value || 6} (High)</span>
                        </div>
                    </div>
                </div>
            </div>

            {/* Activities Section */}
            <div className="activities-section">
                <div className="section-header">
                    <h2>Recommended Activities</h2>
                    <div className="user-preferences-summary">
                        <span className="preferences-text">
                            {userPreferences.getPreferredActivities().length > 0 
                                ? `üéØ ${userPreferences.getPreferredActivities().length} preferences learned`
                                : "üîç Building your preferences..."
                            }
                        </span>
                    </div>
                </div>

                {/* Category Filter */}
                <div className="category-filter">
                    {activityCategories.map(category => (
                        <button
                            key={category}
                            className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
                            onClick={() => setSelectedCategory(category)}
                        >
                            {category}
                        </button>
                    ))}
                </div>

                {/* Activity Cards Grid */}
                <div className="activities-grid">
                    {getFilteredActivities().map(activity => (
                        <div key={activity.id} className="activity-card">
                            <div className="activity-image">
                                <img src={activity.image} alt={activity.name} />
                                <div className="activity-category">{activity.category}</div>
                                {userPreferences.getActivityScore(activity.activity_name || activity.name) > 0 && (
                                    <div className="preference-indicator">‚ù§Ô∏è Liked</div>
                                )}
                            </div>
                            <div className="activity-content">
                                <h3>{activity.name}</h3>
                                <p className="activity-description">{activity.description}</p>
                                <div className="activity-meta">
                                    <div className="rating">
                                        <span className="stars">
                                            {'‚òÖ'.repeat(Math.floor(activity.rating))}
                                            {'‚òÜ'.repeat(5 - Math.floor(activity.rating))}
                                        </span>
                                        <span className="rating-text">
                                            {activity.rating} ({activity.reviews} reviews)
                                        </span>
                                    </div>
                                    <div className="activity-info">
                                        <span className="duration">{activity.duration}</span>
                                        <span className="difficulty">{activity.difficulty}</span>
                                    </div>
                                </div>
                                <div className="activity-actions">
                                    <div className="activity-preferences">
                                        <button 
                                            className="like-btn"
                                            onClick={() => {
                                                userPreferences.likeActivity(activity.activity_name || activity.name);
                                                // Update activity preference indicator
                                            }}
                                            title="Like this activity type"
                                        >
                                            üëç
                                        </button>
                                        <button 
                                            className="dislike-btn"
                                            onClick={() => {
                                                userPreferences.dislikeActivity(activity.activity_name || activity.name);
                                                // Update activity preference indicator
                                            }}
                                            title="Dislike this activity type"
                                        >
                                            üëé
                                        </button>
                                    </div>
                                    <button 
                                        className="view-details-btn"
                                        onClick={() => {
                                            // Store current activities in localStorage for place detail access
                                            localStorage.setItem('recent_activities', JSON.stringify(activities));

                                            // Navigate with place data if available
                                            const place = activity.places && activity.places.length > 0 ? activity.places[0] : null;
                                            navigate(`/place/${activity.id}`, {
                                                state: {
                                                    place: place,
                                                    activityName: activity.activity_name
                                                }
                                            });
                                        }}
                                    >
                                        View Details
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Preferences Reminder */}
            <div className="preferences-cta">
                <h3>Refine Your Recommendations</h3>
                <p>Update your preferences to get even better activity suggestions tailored just for you.</p>
                <button className="goto-preferences-btn">Go to Preferences</button>
            </div>
        </div>
    );
};

export default Dashboard;
